mapper.update:

UPDATE	card_issued
SET		state = "USE",
		expiration_date = Date_ADD(now(), INTERVAL 5 YEAR),
		card_number = #{cardNumber}
WHERE	id = #{id}


UPDATE	card_issued
SET		state = "STOPPING"
WHERE	id = #{id}


UPDATE	card_canceled
SET		cancel_date = now()
WHERE   id = #{id}

mapper.select:

SELECT 		sum(payment) 
FROM		payment 
WHERE		payer        = "USER" 
AND 		target_id    = #{cardIssuedId} 
AND 		target_year  = #{targetYear} 
AND 		target_month = #{targetMonth}
